[{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"using Plots;gr()\nusing SankeyPlots\n\nfunction plot_results(sp_data, pv, w, el_d; plot_window = 1:length(pv), s=1, el_balance_vars=[\"gci\", \"gco\"], storage_vars=[])\n\n    u_pv = sp_data[\"inv\"][\"u_pv\"]\n    u_wind = sp_data[\"inv\"][\"u_wind\"]\n    u_storage = sp_data[\"inv\"][\"u_storage\"]\n    u_heatpump = sp_data[\"inv\"][\"u_heatpump\"]\n    u_heat_storage = sp_data[\"inv\"][\"u_heat_storage\"]\n\n    plt_sto = plot(; legend = :outertopright)\n    plt_invest = plot(; legend = :outertopright)\n    plt = plot(; legend = :outertopright)\n    plot!(plt_invest, plot_window, pv[plot_window] .* u_pv, label=\"PV\")\n    plot!(plt_invest, plot_window, w[plot_window] .* u_wind, label=\"Wind\")\n    plot!(plt_invest, plot_window, el_d[plot_window], label=\"Electrical demand\")\n    t_xi = sp_data[\"scen\"][s][\"t_xi\"]\n    recovery_time = sp_data[\"params\"][\"recovery_time\"]\n\n    stor_charge = sp_data[\"op\"][\"sto_soc\"]\n    plot!(plt_sto, plot_window, stor_charge[plot_window], label=\"global storage charge\")\n    if t_xi in plot_window\n        plot!(plt_sto, (t_xi):(t_xi+recovery_time), sp_data[\"rec\"][s][\"sto_soc2\"], label=string(\"stochastic storage charge\")*string(s), linestyle=:dash, linewidth=2)\n    end\n\n    for var in el_balance_vars\n        plot!(plt, plot_window, sp_data[\"op\"][var][plot_window], label=var)\n        if t_xi in plot_window\n            var2 = var*\"2\"\n            plot!(plt, (t_xi+1):(t_xi+recovery_time + 1), sp_data[\"rec\"][s][var2], label=var*\" 2nd stage, s = \"*string(s), linestyle=:dash, linewidth=2)\n        end\n    end\n    return plot(plt_invest, plt, plt_sto, layout = (3,1))\nend\n\nfunction plot_heat_layer(sp_data, heatdemand; plot_window = 1:length(heatdemand), s = 1)\n    plt_heat = plot(; legend = :outertopright)\n    COP = sp_data[\"params\"][\"COP\"]\n\n    plot!(plt_heat, plot_window, heatdemand[plot_window], label = \"heat demand\")\n    plot!(plt_heat, plot_window, COP*sp_data[\"op\"][\"flow_energy2heat\"][plot_window], label = \"heatpump\")\n    plot!(plt_heat, plot_window, sp_data[\"op\"][\"heat_sto_soc\"][plot_window], label = \"heat storage SOC\")\n    plot!(plt_heat, plot_window, sp_data[\"op\"][\"heat_sto_to_bus\"][plot_window]-sp_data[\"op\"][\"heat_sto_from_bus\"][plot_window], label = \"heat storage use\")","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"plot!(pltheat, (txi+1):(txi+1+recoverytime), COP*spdata[\"rec\"][s][\"flowenergy2heat2\"], label = \"heatpumps\", linestyle=:dash, linewidth=2) plot!(pltheat, (txi+1):(txi+1+recoverytime), spdata[\"rec\"][s][\"heatstotobus2\"]-spdata[\"rec\"][2][\"heatstofrombus2\"], label = \"heat storage use s\", linestyle=:dash, linewidth=2)","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"end\n\nfunction plot_raw_heat_layer(sp_data, heatdemand; plot_span = 1:length(heatdemand), s = 1)\n    plt_heat = plot(; legend = :outertopright)\n    COP = sp_data[\"params\"][\"COP\"]\n\n    #plot!(plt_heat, plot_span, heatdemand[plot_span], label = \"heat demand\")\n    #plot!(plt_heat, plot_span, COP*value.(sp[1, :flow_energy2heat])[plot_span], label = \"heatpump\")\n    plot!(plt_heat, plot_span, sp_data[\"op\"][\"heat_sto_soc\"][plot_span], label = \"heat storage SOC\")\n    plot!(plt_heat, plot_span, sp_data[\"op\"][\"heat_sto_to_bus\"][plot_span], label = \"heat storage to bus\")\n    plot!(plt_heat, plot_span, sp_data[\"op\"][\"heat_sto_from_bus\"][plot_span], label = \"heat storage from bus\")","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"plot!(pltheat, (txi+1):(txi+1+recoverytime), COP*value.(sp[2, :flowenergy2heat2], s), label = \"heatpumps\", linestyle=:dash, linewidth=2) plot!(pltheat, (txi+1):(txi+1+recoverytime), value.(sp[2, :heatstoin2], s)-value.(sp[2, :heatsto_out2], s), label = \"heat storage use s\", linestyle=:dash, linewidth=2)","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"end\n\nfunction plot_recovery_window_deviation(sp_data; s = 1)\n    plt_gc = plot()\n    plt_sto = plot()\n    t_xi = sp_data[\"scen\"][s][\"t_xi\"]\n    recovery_time = sp_data[\"params\"][\"recovery_time\"]\n    plot!(plt_gc, COP*sp_data[\"op\"][\"flow_energy2heat\"][t_xi+1:t_xi+1+recovery_time], label = \"global flow_energy2heat\")\n    plot!(plt_gc, COP*sp_data[\"rec\"][s][\"flow_energy2heat2\"], label = \"stochastic flow_energy2heat\")\n    plot!(plt_gc, -sp_data[\"op\"][\"gci\"][t_xi+1:t_xi+1+recovery_time]+sp_data[\"op\"][\"gco\"][t_xi+1:t_xi+1+recovery_time], label = \"global grid connection use\")\n    plot!(plt_gc, sp_data[\"rec\"][s][\"gco2\"] - sp_data[\"rec\"][s][\"gci2\"], xlabel = \"time after the event, h\", label = \"stochastic grid connection use\")\n    #plot!(plt_gc, -value.(sp[1, :gco])[t_xi+1:t_xi+recovery_time]+value.(sp[2, :gco2], s), xlabel = \"time after the event, h\", label = \"gco2-gco\")\n\n    stor_charge = sp_data[\"op\"][\"sto_soc\"][t_xi+1:t_xi+1+recovery_time]\n    stor_charge2 = sp_data[\"rec\"][s][\"sto_soc2\"]\n\n    plot!(plt_sto, stor_charge2-stor_charge, label = \"soc2-soc\")\n\n\n    plot(plt_gc, plt_sto, layout = (2, 1))\nend\n\nfunction plot_outcome(sp_base, t_xi, s_xi, F_xi; window_start=-2, window_end=2)\n    scen = @scenario t_xi = t_xi s_xi = s_xi F_xi = F_xi probability = 1.\n    sp = outcome_model(sp_base, optimal_decision(sp_base), scen; optimizer = subproblem_optimizer(sp_base))\n    optimize!(sp)\n    if termination_status(sp) != JuMP.MathOptInterface.OPTIMAL\n        println(\"No optimum\")\n        return termination_status(sp)\n    end\n\n    recovery_window = t_xi:t_xi+length(sp[:gco2])-1\n    plot_window = t_xi+window_start:t_xi+length(sp[:gco2])+window_end","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"Some of these should probably be shifted by ±1","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"    plt_gb = plot(title=\"grid buy\", legend=:outertopright)\n    plot!(plt_gb, plot_window, value.(sp[:gci][plot_window]) .- value.(sp[:gco][plot_window]), label = \"Base Case\")\n    plot!(plt_gb, recovery_window, value.(sp[:gci2]) .- value.(sp[:gco2]), label = \"Event\")\n\n    plt_soc = plot(title=\"soc\", legend=:outertopright)\n    plot!(plt_soc, plot_window, value.(sp[:sto_soc][plot_window]), label = \"Base Case\")\n    plot!(plt_soc, recovery_window, value.(sp[:sto_soc2]), label = \"Event\")\n\n    plt_h_soc = plot(title=\"heat soc\", legend=:outertopright)\n    plot!(plt_h_soc, plot_window, value.(sp[:heat_sto_soc][plot_window]), label = \"Base Case\")\n    plot!(plt_h_soc, recovery_window, value.(sp[:heat_sto_soc2]), label = \"Event\")\n\n    plt_e2h = plot(title=\"e2h\", legend=:outertopright)\n    plot!(plt_e2h, plot_window, value.(sp[:flow_energy2heat][plot_window]), label = \"Base Case\")\n    plot!(plt_e2h, recovery_window, value.(sp[:flow_energy2heat2]), label = \"Event\")\n\n    plot(plt_gb, plt_h_soc, plt_soc, plt_e2h, layout=(4,1))\nend\n\nfunction plot_scenario_debug(sp_data, s; window_start=-2, window_end=2, vars = [\"gci\", \"gco\", \"sto_soc\", \"heat_sto_soc\", \"flow_energy2heat\"])\n\n    t_xi = sp_data[\"scen\"][s][\"t_xi\"]\n    recovery_time = sp_data[\"params\"][\"recovery_time\"]\n\n    recovery_window = t_xi:t_xi+recovery_time\n    plot_window = t_xi+window_start:t_xi+recovery_time+window_end\n\n    plots = []\n\n    for var in vars\n        plt_var = plot(title=var, legend=:outertopright)\n\n\n        plot!(plt_var, plot_window, sp_data[\"op\"][var][plot_window], label = \"Base Case\")\n        plot!(plt_var, recovery_window, sp_data[\"rec\"][s][var*\"2\"][plot_window], label = \"Event\")\n\n        push!(plots, plt_var)\n    end\n\n    plot(plots...; layout=(length(plots),1), size = (800, 150*length(plots)))\nend\n\nfunction plot_base_case_raw(sp_data; plot_window = nothing, vars = [\"gci\", \"gco\", \"sto_soc\", \"heat_sto_soc\", \"flow_energy2heat\"])\n\n    if isnothing(plot_window)\n        plot_window = 1:length(sp_data[\"op\"][\"gci\"])\n    end\n\n    plots = []\n\n    for var in vars\n        plt_var = plot(title=var, legend=:none)\n\n        plot!(plt_var, plot_window, sp_data[\"op\"][var][plot_window])\n\n        push!(plots, plt_var)\n    end\n\n    plot(plots...; layout=(length(plots),1), size = (800, 150*length(plots)))\nend\n\n\nfunction plot_outcome_debug(sp_base, t_xi, s_xi, F_xi; window_start=-2, window_end=2, vars = [\"gci\", \"gco\", \"sto_soc\", \"heat_sto_soc\", \"flow_energy2heat\"])\n    scen = @scenario t_xi = t_xi s_xi = s_xi F_xi = F_xi probability = 1.\n    sp = outcome_model(sp_base, optimal_decision(sp_base), scen; optimizer = subproblem_optimizer(sp_base))\n    optimize!(sp)\n    if termination_status(sp) != JuMP.MathOptInterface.OPTIMAL\n        println(\"No optimum\")\n        return termination_status(sp)\n    end\n    recovery_time = length(sp[:gco2])-1\n\n    recovery_window = t_xi:t_xi+recovery_time\n    plot_window = t_xi+window_start:t_xi+recovery_time+window_end\n\n    plots = []\n\n    for var in vars\n        plt_var = plot(title=var, legend=:outertopright)\n        symbol_base_case = Symbol(var) # e.g. :sto_soc\n        symbol_event_case = Symbol(var * \"2\") # e.g. :sto_soc2\n\n        plot!(plt_var, plot_window, value.(sp[symbol_base_case][plot_window]), label = \"Base Case\")\n        plot!(plt_var, recovery_window, value.(sp[symbol_event_case]), label = \"Event\")\n\n        push!(plots, plt_var)\n    end\n\n    plot(plots...; layout=(length(plots),1), size = (800, 150*length(plots)))\nend\n\nfunction sankey_results(sp, pv, w, el_d, timesteps)\n    total_pv = value.(sp[1, :u_pv])*sum(pv[timesteps])\n    total_wind = value.(sp[1, :u_wind])*sum(w[timesteps])\n    total_demand = sum(el_d[timesteps])\n    pv_cur = sum(value.(sp[1,:pv_cur])[timesteps])\n    wind_cur = sum(value.(sp[1,:wind_cur])[timesteps])\n    st_in = sum(value.(sp[1,:sto_to_bus])[timesteps])\n    st_out = sum(value.(sp[1,:sto_from_bus])[timesteps])\n    grid_in = sum(value.(sp[1,:gci])[timesteps])\n    grid_out = sum(value.(sp[1,:gco])[timesteps])\n    energy2heat = sum(value.(sp[1,:flow_energy2heat])[timesteps])\n    losses_charge = 1/sp.stages[1].parameters[:sto_ef_ch] - 1.\n    losses_discharge = 1. - sp.stages[1].parameters[:sto_ef_dis]\n    storage_losses = losses_charge*sum(value.(sp[1,:sto_from_bus])[timesteps]) + losses_discharge*sum(value.(sp[1,:sto_to_bus])[timesteps])\n    labels = [\"PV\", \"Wind\", \"Storage (input)\", \"Storage (output)\", \"Demand\", \"Grid input\", \"Grid output\", \"Bus\", \"Losses\", \"Energy to heat\", \"Curtailment\"]\n    src = [1,1,2,2,4,6,8,8,8,8,8]\n    trg = [8,11,8,11,8,8,3,5,7,9,10]\n    weights = [total_pv-pv_cur, pv_cur, total_wind-wind_cur, wind_cur, st_in, grid_in, st_out, total_demand, grid_out, storage_losses, energy2heat]\n    total_in = total_pv+total_wind+st_in+grid_in-pv_cur-wind_cur\n    total_out = total_demand+st_out+grid_out+energy2heat\n    println(\"relative mismatch = $((total_in-total_out)/total_in)\")\n    #println(storage_losses/(st_in+st_out))\n    sankey(src, trg, weights, node_labels = labels)\nend\n\nfunction plot_scenario_distribution(scenarios; by_sign = false)\n    if !by_sign\n        scen_matrix = zeros((24,365))\n        for s in scenarios\n            t_xi = s.data.t_xi\n            scen_matrix[mod(t_xi,24)+1,t_xi÷24+1] = 1\n        end\n        heatmap(scen_matrix, leg=false,\n        xlabel = \"Day of the year\",\n        ylabel = \"Hour of the day\", title = \"Ancillary requests sample\")\n    else\n        scen_matrix_pos = zeros((24,365))\n        scen_matrix_neg = zeros((24,365))\n\n        for s in scenarios\n            t_xi = s.data.t_xi\n            s_xi = s.data.s_xi\n            F_xi = s.data.F_xi\n            if s_xi == 1\n                scen_matrix_pos[mod(t_xi,24)+1,t_xi÷24+1] = F_xi\n            else\n                scen_matrix_neg[mod(t_xi,24)+1,t_xi÷24+1] = F_xi\n            end\n        end\n        p = plot(layout = (1,2))\n        plot!(p[1],scen_matrix_pos, seriestype = :heatmap, ratio = 365/24, framestyle = :none)\n        plot!(p[2],scen_matrix_neg, seriestype = :heatmap, ratio = 365/24, framestyle = :none)\n    end\nend\n\nfunction plot_flex_sources(sp_data, pv, wind; timesteps = 1:24*365)\n    pos_cur = sp_data[\"op\"][\"pv_cur\"][timesteps[begin:end-1]] .+ sp_data[\"op\"][\"wind_cur\"][timesteps[begin:end-1]]\n    neg_cur = pos_cur .- pv[timesteps[begin:end-1]].*sp_data[\"inv\"][\"u_pv\"] .- wind[timesteps[begin:end-1]].*sp_data[\"inv\"][\"u_wind\"]\n    pos_sto = sp_data[\"op\"][\"sto_soc\"][timesteps[begin+1:end]]\n    neg_sto = pos_sto .- sp_data[\"inv\"][\"u_storage\"]\n    COP = COP = sp_data[\"params\"][\"COP\"]\n    pos_heat_sto = sp_data[\"op\"][\"heat_sto_soc\"][timesteps[begin+1:end]]/COP\n    neg_heat_sto = pos_heat_sto .- sp_data[\"inv\"][\"u_heat_storage\"]/COP\n    #=plt = plot(layout = (1,2))\n    plot!(plt[1], pos_cur, fill = (0, 0.5))\n    plot!(plt[1], pos_sto, fill = (0, 0.5))\n    plot!(plt[1], pos_heat_sto, fill = (0, 0.5))\n    plot!(plt[2], neg_cur, fill = (0, 0.5))\n    plot!(plt[2], neg_sto, fill = (0, 0.5))\n    plot!(plt[2], neg_heat_sto, fill = (0, 0.5))=#\n    p1 = areaplot(timesteps[begin:end-1], [pos_cur, pos_sto, pos_heat_sto], fillalpha = [0.5 0.5 0.5])\n    p2 = areaplot(timesteps[begin:end-1], [-neg_cur, -neg_sto, -neg_heat_sto], fillalpha = [0.5 0.5 0.5])\n    plot(p1, p2, layout = (1,2))\nend","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"","category":"page"},{"location":"generated/srcs/plot_utils/","page":"Plotting","title":"Plotting","text":"This page was generated using Literate.jl.","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"using Plots\n\n\"\"\"\nSuppress output of evaluate_decision, and deal with solvers that error on infeasibility.\n\"\"\"\nfunction evaluate_decision_wrapper(p, decision, scenario)\n    cost = 0.\n    try redirect_stdout((() -> cost = evaluate_decision(p, decision, scenario)), devnull)\n    catch e\n        cost = Inf\n    end\n    return cost\nend\n\n\"\"\"\nFind maximum available flexibility and its cost at all points in some time interval.\n\"\"\"\nfunction analyze_flexibility_potential(sp, timesteps, penalty_cost, no_flex_cost; decision = optimal_decision(sp))\n    cost_no_flex = evaluate_decision_wrapper(sp, optimal_decision(sp), no_flex_pseudo_sampler()[1])\n    L = length(timesteps)\n    cost_pos_flex = zeros(L)\n    cost_neg_flex = zeros(L)\n    potential_pos_flex = zeros(L)\n    potential_neg_flex = zeros(L)\n    Threads.@threads for i in eachindex(timesteps)\n        t = timesteps[i]\n        potential_pos_flex[i], cost_pos_flex[i] = find_f_max(sp,t,1,decision, penalty_cost, no_flex_cost)\n        potential_neg_flex[i], cost_neg_flex[i] = find_f_max(sp,t,-1,decision, penalty_cost, no_flex_cost)\n    end\n    return cost_pos_flex .- cost_no_flex, potential_pos_flex, cost_neg_flex .- cost_no_flex, potential_neg_flex\nend\n\nfunction plot_flexibility(timesteps, cost_pos_flex, potential_pos_flex, cost_neg_flex, potential_neg_flex)\n    plt_cost = plot()\n    plt_pot = plot()\n    plot!(plt_cost, timesteps, cost_pos_flex ./ potential_pos_flex, label = \"price of positive flexibility\")\n    plot!(plt_cost, timesteps, cost_neg_flex ./ abs.(potential_neg_flex), label = \"price of negative flexibility\")\n    plot!(plt_pot, timesteps, potential_pos_flex, fillrange = 0, fillalpha = 0.35, label = \"positive flexibility potential\")\n    plot!(plt_pot, timesteps, potential_neg_flex, fillrange = 0, fillalpha = 0.35, label = \"negative flexibility potential\")\n    plot(plt_cost, plt_pot, layout = (2, 1))\nend\n\n\"\"\"\nFind distribution of maximum available flexibilities.\n\"\"\"\nfunction flexibility_availability!(plt, flex_potential; plot_options...)\n    p_sorted = sort(unique(flex_potential))\n    fraction = [sum(abs.(flex_potential[:]).>=abs(f)) for f in p_sorted]./length(p_sorted)\n    plot!(plt, p_sorted, fraction)\nend\n\n\"\"\"\nBisection search for maximum felxibility potential.\nParameters:\n- sp - optimized stochastic program\n- t - time step\n- s - sign of flexibility scenario\n- tol - result tolerance\n- maxiters - maximum number of iterations\n\"\"\"\nfunction find_f_max(sp, t, s, od, penalty_cost, no_flex_cost; tol = 10., maxiter = 500)\n    a = 0.\n    b = 10000.\n    cost_a = 0.\n    cost_b = 0.\n    i = 0\n    scen = @scenario t_xi = t s_xi = s F_xi = a probability = 1.\n    cost_a = evaluate_decision_wrapper(sp,od,scen)\n    if cost_a == Inf\n        return 0., cost_a\n    else\n        while b-a>tol && i<=maxiter && cost_a < no_flex_cost + penalty_cost# gi != 0, ...\n            i+=1\n            scen = @scenario t_xi = t s_xi = s F_xi = b probability = 1.\n            cost_b = evaluate_decision_wrapper(sp,od,scen)\n            if cost_b == Inf || cost_b > no_flex_cost + penalty_cost\n                b = (a+b)/2\n            else\n                a = b\n                cost_a = cost_b\n                b *= 2\n            end\n        end\n        return s*a, cost_a\n    end\nend\n\nfunction evaluate_cost(sp, decision, timesteps, F)\n    cost_pos = zeros(length(timesteps))\n    cost_neg = zeros(length(timesteps))\n    Threads.@threads for i in eachindex(timesteps)\n        s_pos = @scenario t_xi = timesteps[i] s_xi =  1 F_xi = F\n        s_neg = @scenario t_xi = timesteps[i] s_xi = -1 F_xi = F\n        cost_pos[i] = evaluate_decision_wrapper(sp, decision, s_pos)\n        cost_neg[i] = evaluate_decision_wrapper(sp, decision, s_neg)\n    end\n    return cost_pos, cost_neg\nend\n\nfunction evaluate_cost_penalty(sp, t, s, F, penalty_cost; decision = optimal_decision(sp))\n    scen = @scenario t_xi = t s_xi = s F_xi = F\n    outcome = outcome_model(sp, decision, scen; optimizer = subproblem_optimizer(sp))\n    set_silent(outcome)\n    optimize!(outcome)\n    cost = objective_value(outcome)\n    penalty = (maximum([value(outcome[:gi1]), value(outcome[:gi2])]) + maximum([value(outcome[:go1]), value(outcome[:go2])]))*penalty_cost\n    return cost, penalty\nend\n\n#= We use investment and operation dictionaries rather than getting the data directly from the model,\nas this function is meant for post-processing\n=#\nfunction naive_flex_potential(invs, ops, pv, wind, pars, timesteps)\n    pv_cur = ops[:pv_cur]\n    wind_cur = ops[:wind_cur]\n    u_storage = invs[:u_storage]\n    u_heat_storage = invs[:u_heat_storage]\n    u_pv = invs[:u_pv]\n    u_wind = invs[:u_wind]\n    sto_soc = ops[:sto_soc]\n    heat_soc = ops[:heat_sto_soc]\n    COP = pars[:COP]\n    F_pos = zeros(length(timesteps[1:end-12]))\n    F_neg = zeros(length(timesteps[1:end-12]))\n    for t in timesteps[1:end-12]","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"positive flexibility:","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"        F_pos[t] = pv_cur[t] + wind_cur[t] + sto_soc[t+1] + heat_soc[t+1]/COP","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"negative flexibility:","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"        F_neg[t] = pv_cur[t] + wind_cur[t] - pv[t]*u_pv - wind[t]*u_wind + sto_soc[t+1] - u_storage + (heat_soc[t+1] - u_heat_storage)/COP\n    end\n    return F_pos, F_neg.* (-1.)\nend","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"","category":"page"},{"location":"generated/srcs/evaluation_utils/","page":"Evaluation","title":"Evaluation","text":"This page was generated using Literate.jl.","category":"page"},{"location":"#Stochastic-Flexibility-Optimization","page":"Flexibility analysis using Stochastic Programing","title":"Stochastic Flexibility Optimization","text":"","category":"section"},{"location":"","page":"Flexibility analysis using Stochastic Programing","title":"Flexibility analysis using Stochastic Programing","text":"Companion repo for paper.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"EditURL = \"https://github.com/PIK-ICoNe/StochasticFlexibility/blob/main/manuscripts/flexibility_by_stochastic_programing.jl\"","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"# Everything runs in the Project environment on the basepath\n\nbasepath = realpath(joinpath(\"/home/runner/work/StochasticFlexibility/StochasticFlexibility/manuscripts\", \"..\"))\n\nusing Pkg\nPkg.activate(basepath)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Pkg.instantiate()","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"using DataFrames\nusing CSV\nusing Clp\nusing Statistics;\n\nusing Random\nRandom.seed!(1);\nnothing #hide","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/#Flexibility-analysis-and-optimization","page":"Main Story","title":"Flexibility analysis and optimization","text":"","category":"section"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We take an energy system and analyze and optimize its flexibility potential.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"The model is defined in the file sp_model.jl, analysis and utility functions are in the other two files.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"The parametrization is not completely done yet, and running the analysis for a whole year is very time intensive. The main optimization runs fine for a whole year, but analyzing the flexibility potential for many hours is computationally intense.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We need to either start caching more computations, or find a way to speed up the evaluation further.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This file gives a conceptual demonstration of what we do on a two week horizon.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"include(joinpath(basepath, \"src\", \"sp_model.jl\"))\ninclude(joinpath(basepath, \"src\", \"plot_utils.jl\"))\ninclude(joinpath(basepath, \"src\", \"evaluation_utils.jl\"))\ninclude(joinpath(basepath, \"src\", \"data_load.jl\"));\nnothing #hide","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We load timeseries for photovoltaic (pv) and wind potential as well as demand.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"timesteps = 1:(24*365)\npv, wind, demand, heatdemand = load_basic_example(timesteps);\n\nplt = plot(timesteps, pv .* (mean(demand) / mean(pv)), label=\"PV (unitless)\")\nplot!(plt, timesteps, wind.* (mean(demand) / mean(wind)), label=\"Wind (unitless)\")\nplot!(plt, timesteps, heatdemand, label=\"Heat Demand\")\nplot!(plt, timesteps, demand, label=\"Electric Demand\")\nplt","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Next we continue the set up. Our model comes with default parameters, which we slightly adjust here. We use some arbitrary values to define a dummy heat demand.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"pars = copy(defaultespars)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"averagehourlydemand = mean(demand)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"pars[:recoverytime] = 24 pars[:cstorage] = 100. pars[:cpv] = 300. pars[:cwind] = 550. pars[:cstoop] = 0.00001;","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"pars[:] #-","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"pars = copy(default_es_pars)\n\naverage_hourly_demand = mean(demand)\n\nrecovery_time = 12\n\npars[:recovery_time] = recovery_time\npars[:c_storage] = 100.\npars[:c_pv] = 300.\npars[:c_wind] = 550.\npars[:penalty] = 1000000.;\n\nt_max = length(pv) - 24\nF_max = 10000.\nF_min = 3000.\ndelta_t = 3*24 # Flex event every week\npars[:event_per_scen] = t_max / (delta_t + recovery_time + 1);\nn = round(Int, 10 * pars[:event_per_scen])\n\nscens = poisson_events_with_offset(n, delta_t, recovery_time, F_max, t_max, F_min = F_min)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"The model itself is constructed by the function defineenergysystem","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"es_bkg = define_energy_system(pv, wind, demand, heatdemand; p = pars, regularized = false, override_no_event_per_scen = true)\nes_no_reg = define_energy_system(pv, wind, demand, heatdemand; p = pars, regularized = false)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This contains investment variables which we collectively call I, and the operational schedule O^t. The total cost given a certain investment I and schedule O^t is denoted C(I O^t).","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We now can optimize the system, initialy while ignoring flexibility:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"sp_bkg = instantiate(es_bkg, no_flex_pseudo_sampler(), optimizer = Clp.Optimizer)\n\nset_silent(sp_bkg)\n\noptimize!(sp_bkg)\n\nbkg_decision = optimal_decision(sp_bkg)\n\nobjective_value(sp_bkg)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"overline I_nf  overline O^t_nf = argmin_I O^t C(I O^t)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can now analyse how much flexibility is available at each point in time with this investment and this schedule.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"To do so we distinguish between parts of the operational schedule that can react to a flexibility demand in the moment that it occurs, and those that can only adapt later to help recover the system back towards its previously planned schedule. Essentially into slow components O^st and fast components O^ft","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"O^t = (O^ft O^st)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We then consider the cost of the system when confronted with an additional negative or positive demand F at some timepoint t^F. At the timepoint t^F only the fast reacting components can adjust their schedule, the slow components still have to stick to the previous schedule. After the event we give the system a time t_r to recover back to its original schedule. Here the slow components can contribute. Thus we have:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"beginaligned\nc(Ft^F) = min_O^t C(overline I_nf O^t F t_f)  \nO^st = overline O^t_nf  forall  t leq t^F   t  t^F + t_r \nO^ft = overline O^t_nf  forall   t  t^F   t  t^F + t_r\nendaligned","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This is the cost of flexibility in the sense of Harder et.al. This optimization is not always feasible. The maximum and minimum F for which it is feasible is called the positive and negative flexibility potential pot_pm. The marginal cost of flexibility is cost_pm(t) = c(pot_pm(t) t)  pot_pm(t). Using our model above we can analyze this in the following way:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"analysis_window = 190+1:190+48","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"costpos, potpos, costneg, potneg = analyzeflexibilitypotential(spnoflex, analysis_window)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"plotflexibility(analysiswindow, costpos, potpos, costneg, potneg)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"plot_results(sp_bkg, pv, wind, demand)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"plot_heat_layer(sp_bkg, heatdemand)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can analyze individual scenarios by using the evaluate decision function. This allows us to test the system on scenarios that were not present in the original problem definition","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"To get a model that corresponds to a single flexibility event (e.g. to investigate how the system is reacting to any one event) we can construct outcome models as below. These are normal JuMP models.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"prob_scen = @scenario t_xi = 195 s_xi = 1. F_xi = 20. probability = 1.\nevaluate_decision(sp_bkg, bkg_decision, prob_scen)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"outcome = outcome_model(sp_bkg, bkg_decision, prob_scen; optimizer = subproblem_optimizer(sp_bkg))\nset_silent(outcome)\noptimize!(outcome)\ntermination_status(outcome)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Under the hood this uses the evaluate_decision function of stochastic programs that evaluates the cost of a specified scenario given a decision.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can go further if we introduce a certain distribution of flexibility demands. Then we can choose to operate the system in such a way that the expected cost, including the expected cost of flexibility, is minimized. This is exactly a two stage stochastic programming problem with c(Ft) as the second stage.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can start by taking a simple uniform distribution between some maximum flexibility demand that can occur at any time that leaves enough space for the recovery window:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"n = 100 Fmax = averagehourlydemand * 0.1 # Have unaticipated demand equal to 10% of our typical demand tmax = length(pv) - es.parameters[2].defaults[:recovery_time]","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"scens = simpleflexsampler(n, Fmax, tmax);","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can now evaluate the expected cost of running the system determined above with this flexibility distribution.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"sp_no_reg = instantiate(es_no_reg, scens, optimizer = Clp.Optimizer)\nset_silent(sp_no_reg)\n\nevaluate_decision(sp_no_reg, bkg_decision)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This is infinite as the system as built above can not actually provide the desired flexibility at all times. One way to deal with this problem is to regularize the problem, by allowing a heavily penalized deviation from satisfying the extra demand.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"es_reg = define_energy_system(pv, wind, demand, heatdemand; p = pars, regularized = true)\n\nsp_reg = instantiate(es_reg, scens, optimizer = Clp.Optimizer)\nset_silent(sp_reg)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Now we can evaluate the expected cost of flexibility in the system:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"evaluate_decision(sp_reg, bkg_decision)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"TODO / BUG: This is infinite for some time windows/seeds! This should not happen. (SOLVED?!)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This cost is of course completely dominated by the regularizer. However, using stochastic programming we can optimize the operational schedule to directly optimize this expected cost. To do so we fix the investment to the system we have and then optimize the remaining variables:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"investments_nf = get_investments(sp_bkg)\nfix_investment!(sp_no_reg, investments_nf)\nfix_investment!(sp_reg, investments_nf)\n\noptimize!(sp_no_reg)\noptimize!(sp_reg)\n\ntermination_status(sp_no_reg) # Infeasible with F_max 10000 and 1000\ntermination_status(sp_reg)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"noregnoinvestdecision = optimaldecision(spno_reg)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"reg_no_invest_decision = optimal_decision(sp_reg);\nnothing #hide","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"prob = 1/length(scens) is_optimal = zeros(Bool, length(scens))","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Threads.@threads for i in eachindex(scens)     println(i)     s = scens[i]     snew = @scenario txi = s.data[:txi] sxi = s.data[:sxi] Fxi = 10000. probability = prob     scenariomodel = outcomemodel(spnoreg, optimaldecision(spreg), snew; optimizer = subproblemoptimizer(spreg))     setsilent(scenariomodel)     optimize!(scenariomodel) # Primal infeasible     # println(terminationstatus(scenariomodel))     # println(snew)     isoptimal[i] = terminationstatus(scenariomodel) == MOI.TerminationStatusCode(1) end","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Then evaluating the decision we find much more reasonable values:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"evaluate_decision(sp_reg, no_reg_no_invest_decision)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"evaluate_decision(sp_reg, reg_no_invest_decision)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"no_invest_relative_cost_of_flex = evaluate_decision(sp_reg, reg_no_invest_decision) / objective_value(sp_bkg) - 1.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"It is interesting to note that this way servicing the flexibility is actually not very expensive compared to the baseline cost of the system if there were no flexibility demands, with the relative cost of flexibility coming in at 0.2% here.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can also again evaluate the amount of flexibility available at each point in time, and the associated cost:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"To find flexibility potentials we right now have to use the unregularized model: costposflex, potposflex, costnegflex, potnegflex = analyzeflexibilitypotential(spflex, analysiswindow)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"plotflexibility(analysiswindow, costposflex, potposflex, costnegflex, potnegflex)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This shows that the model is not actually able to guarantee that there is flexibility at all times. However, it dramatically increases the amount of available flexibility:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"pltav = plot(); flexibilityavailability!(pltav, potpos, label = \"positive flexbility unaware\", c = :red); flexibilityavailability!(pltav, potposflex, label = \"positive flexibility aware\", c = :green); flexibilityavailability!(pltav, potneg, label = \"negative flexibility unaware\", c = :red); flexibilityavailability!(pltav, potnegflex, label = \"negative flexibility aware\", c = :green); pltav","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"We can of course also optimize the overall system investment to take flexibility into account.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"unfix_investment!(sp_no_reg, investments_nf)\nunfix_investment!(sp_reg, investments_nf)\n\noptimize!(sp_no_reg)\noptimize!(sp_reg)\n\ntermination_status(sp_no_reg)\ntermination_status(sp_reg)\n\nno_reg_invest_decision = optimal_decision(sp_no_reg)\nreg_invest_decision = optimal_decision(sp_reg);\nnothing #hide","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Then the relative cost of the system exposed to flexibility is further reduced:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"evaluate_decision(sp_reg, no_reg_invest_decision)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"evaluate_decision(sp_reg, reg_invest_decision)","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"invest_relative_cost_of_flex = evaluate_decision(sp_reg, reg_invest_decision) / objective_value(sp_bkg) - 1.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Considering the flexiblity demands at investment time, rather than only during operations lowers the cost of flexibility by:","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"invest_relative_cost_of_flex / no_invest_relative_cost_of_flex - 1.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"27% for this toy model.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/#Further-thoughts","page":"Main Story","title":"Further thoughts","text":"","category":"section"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"There are numerous directions to go from here.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This document focused on (a regularized version of) the expected price of flexibility. Another possibility would be to make a minimum amount of available flexibility a constraint. There are algorithms taht concern stochastic constraints that might be useful to explore then.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"A general question with all of this is: We are sampling the space of possible events. The operational schedule we obtain will probably contain moments at which there is no flexibility. The sample might miss the few hours at which flexibility is hardest to come by.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"It is unclear (and requires further analysis) how much of a problem that is. E.g. maybe the flex aware schedule based on a sample will not have flexibility at some hours, but a very minor adjustment would. In partiuclar I consider it plausible that the investment decisions are not strongly affected by these gaps, as long as sufficiently many representative scenarios are sampled.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"In evaluating the quality of the sampling approach it might also be appropriate to evaluate the assumption of a schedule based on perfect foresight for everything but the felxiblity. A proper validation set up would also consider the weather and demand uncertainties.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"A general choice we have is whether to think of the flexibility as for the system itself, or for selling as an auxilliary service. In the former case we can think of the regularizer as buying flexibility on the open market once providing it ourselfs becomes to expensive. For the latter we would need to think about potential products that can reasonably be offered/modelled in the stochastic program. E.g. we can not simply say we are offering the full flexibility potential as that is a quantity that is hard to evaluate.","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"Given multiple energy systems that trade with each other we could try to analyze a market of such systems as well","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"","category":"page"},{"location":"generated/flexibility_by_stochastic_programing/","page":"Main Story","title":"Main Story","text":"This page was generated using Literate.jl.","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"using StochasticPrograms\nusing Random\nusing Distributions\n\n#\n\"\"\"\nDefault values of system parameters.\n- c_i - price of buying energy from the grid, Euro/kW\n- c_o - price of selling energy to the grid, Euro/kW\n- asset_lifetime - expected lifetime of a component in years. Needed to bring investment and operational costs to the same timescale\n- c_pv, c_wind - price of installing pv and wind components per kW peak, Euro/kWp\n- c_storage - price of storage, Euro/kW\n- c_heat_storage - TODO: heat layer units\n- c_heatpump\n- inv_budget - maximum investment budget, Euro\n- recovery_time - time after the event, in which the system is allowed to deviate from background optimal schedule, h\n- COP - heatpump efficiency coefficient\n- heat_losses - losses in the heat storage\n- penalty - price of non-delivery of flexibility, Euro\n\"\"\"\ndefault_es_pars = Dict((\n    :c_i => .3,\n    :c_o => .05,\n    :asset_lifetime => 10.,\n    :c_pv => 700.,\n    :c_wind => 2000.,\n    :c_storage => 600.,\n    :c_heat_storage => 30.,\n    :c_heatpump => 20.,\n    :inv_budget => 500000000.,\n    :recovery_time => 12,\n    :COP => 3.5,\n    :feedincap => 10^7,\n    :heat_losses => 0.2,\n    :heat_eff => 0.9,\n    :sto_ef_ch => 0.95,\n    :sto_ef_dis => 0.95,\n    :penalty => 10000.,\n    :event_per_scen => 1,\n    :max_sto_flow => 0.2,\n    :max_pv => 10^3,\n    :max_wind => 10^3\n))\n\n\"\"\"\nDraw a sample of n scenarios\n\"\"\"\nfunction simple_flex_sampler(n, F_max, t_max; F_min = 0.)\n    [@scenario t_xi = rand(1:t_max) s_xi = rand([-1, 1]) F_xi = F_min + rand() * (F_max - F_min) probability = 1/n\n        for i in 1:n]\nend\n\n\"\"\"\nExponentially distributed inter event times, with the inter event time counting\nfrom after the recovery time. Average inter event time should be delta_t + recovery_time + 1\n\"\"\"\nfunction poisson_events_with_offset(n, delta_t, recovery_time, F_max, t_max; F_min = 0.)\n    @assert t_max > delta_t\n    times = []\n    t = 1\n    interval_distribution = Exponential(delta_t)\n    while length(times) < n\n        t += recovery_time + 1\n        t += round(Int, rand(interval_distribution))\n        push!(times, t)\n    end\n    times = times .% t_max .+ 1\n    [@scenario t_xi = t s_xi = rand([-1, 1]) F_xi = F_min + rand() * (F_max - F_min) probability = 1/n\n    for t in times]\nend\n\n\n\"\"\"\nGet an array with a single pseudo scenario with no flexiblity.\nThis is useful for optimizing the system as if no flexibilty was introduced.\n\"\"\"\nfunction no_flex_pseudo_sampler()\n    [@scenario t_xi = 1 s_xi = 1 F_xi = 0. probability = 1.\n    ,]\nend\n\n\"\"\"\nDraw a sample of n scenarios which have different probaility at day and night\n\"\"\"\n\nfunction timedependent_flex_sampler(n, F_max, t_day, t_night, prob_day, prob_night)\n    sday = [@scenario t_xi = rand(t_day) s_xi = rand([-1, 1]) F_xi = rand() * F_max probability = prob_day/n\n        for i in 1:(n/2)]\n    snight = [@scenario t_xi = rand(t_night) s_xi = rand([-1, 1]) F_xi = rand() * F_max probability = prob_night/n\n        for i in 1:(n/2)]\n    return vcat(sday,snight)\nend\n\n\"\"\"\nDefine energy system.\nParameters:\n- pv, wind - weather timeseries\n- demand, heatdemand - demand timeseries\n- p - dictionary with system parameters, such as component costs, losses and recovery time window\n- regularized - bool, if true, finite penalty is used\n\"\"\"\nfunction define_energy_system(pv, wind, demand, heatdemand; p = default_es_pars, regularized = true, debug_cap = 10^9, reg_lossy_flows = 0.0000001, override_no_event_per_scen = false, guaranteed_flex = false, F_pos = 1000., F_neg = -1000.)\n    number_of_hours = minimum([length(pv), length(demand), length(wind)])\n    if override_no_event_per_scen\n        event_per_scen = 1\n    else\n        event_per_scen = p[:event_per_scen]\n    end\n    energy_system = @stochastic_model begin\n        @stage 1 begin\n            @parameters begin","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Expected lifetime of components, years","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"                asset_lifetime = p[:asset_lifetime]","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Costs in Euro/1kWp","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"                c_pv = p[:c_pv]\n                c_wind = p[:c_wind]\n                c_storage = p[:c_storage]\n                c_i = p[:c_i]\n                c_o = p[:c_o]\n                c_heat_storage = p[:c_heat_storage]\n                c_heatpump = p[:c_heatpump]\n                COP = p[:COP]\n                heat_losses = p[:heat_losses]\n                heat_eff = p[:heat_eff]\n                sto_ef_ch = p[:sto_ef_ch] # efficiency of storage charge (from bus)\n                sto_ef_dis = p[:sto_ef_dis] # efficiency of storage discharge\n                feedincap = p[:feedincap]\n                max_sto_flow = p[:max_sto_flow] # relative cap of charge/discharge in one hour\n                event_per_scen = event_per_scen","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Euro","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"                inv_budget = p[:inv_budget] # Make the problem bounded\n                max_pv = p[:max_pv]\n                max_wind = p[:max_wind]\n                regularize_lossy_flows = reg_lossy_flows\n            end\n            lifetime_factor = asset_lifetime * 365 * 24 / number_of_hours","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Component units to be invested in, kWp","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @decision(model, u_pv >= 0)\n            @decision(model, u_wind >= 0)\n            @decision(model, u_storage >= 0)\n\n            @constraint(model, u_pv <= max_pv)\n            @constraint(model, u_wind <= max_wind)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Curtailment","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @decision(model, 0 <= pv_cur[t in 1:number_of_hours] <= debug_cap)\n            @decision(model, 0 <= wind_cur[t in 1:number_of_hours] <= debug_cap)\n            @constraint(model, [t in 1:number_of_hours], pv_cur[t] <= u_pv * pv[t])\n            @constraint(model, [t in 1:number_of_hours], wind_cur[t] <= u_wind * wind[t])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Grid connection","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @decision(model, 0 <= gci[t in 1:number_of_hours] <= debug_cap)\n            @decision(model, 0 <= gco[t in 1:number_of_hours] <= debug_cap)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Feedin cap","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, [t in 1:number_of_hours], gco[t] <= feedincap)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Storage model","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @decision(model, 0 <= sto_to_bus[t in 1:number_of_hours] <= debug_cap) # into the bus from storage\n            @decision(model, 0 <= sto_from_bus[t in 1:number_of_hours] <= debug_cap)\n            @decision(model, 0 <= sto_soc[t in 1:number_of_hours] <= debug_cap)\n            @constraint(model, [t in 1:number_of_hours-1], sto_soc[t+1] == sto_soc[t] + sto_from_bus[t] * sto_ef_ch - sto_to_bus[t] / sto_ef_dis)\n            @constraint(model, [t in 1:number_of_hours], sto_soc[t] <= u_storage)\n            @constraint(model, [t in 1:number_of_hours], sto_from_bus[t] <= max_sto_flow * u_storage)\n            @constraint(model, [t in 1:number_of_hours], sto_to_bus[t] <= max_sto_flow * u_storage)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Start and end condition","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, sto_soc[1] == u_storage / 2)\n            @constraint(model, sto_soc[number_of_hours] + sto_from_bus[number_of_hours] * sto_ef_ch- sto_to_bus[number_of_hours] / sto_ef_dis == sto_soc[1])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Heat model","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @decision(model, u_heatpump >= 0)\n            @decision(model, u_heat_storage >= 0)\n            @decision(model, 0 <= heat_sto_to_bus[t in 1:number_of_hours] <= debug_cap) # to the heat storage\n            @decision(model, 0 <= heat_sto_from_bus[t in 1:number_of_hours] <= debug_cap)\n            @decision(model, 0 <= heat_sto_soc[t in 1:number_of_hours] <= debug_cap)\n            @decision(model, 0 <= flow_energy2heat[t in 1:number_of_hours] <= debug_cap)\n            @constraint(model, [t in 1:number_of_hours-1], heat_sto_soc[t+1] == heat_sto_soc[t] + heat_sto_from_bus[t] * heat_eff - heat_sto_to_bus[t] / heat_eff)\n            @constraint(model, [t in 1:number_of_hours], heat_sto_soc[t] <= u_heat_storage)\n            @constraint(model, [t in 1:number_of_hours], flow_energy2heat[t] <= (1/COP)*u_heatpump)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Start and end condition","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, heat_sto_soc[1] == u_heat_storage / 2)\n            @constraint(model, heat_sto_soc[number_of_hours] + heat_sto_from_bus[number_of_hours] * heat_eff - heat_sto_to_bus[number_of_hours] / heat_eff == heat_sto_soc[1])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Investment budget","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, inv_budget, c_pv * u_pv + c_wind * u_wind + c_storage * u_storage\n            + c_heat_storage * u_heat_storage + c_heatpump * u_heatpump <= inv_budget)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Energy balance","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, [t in 1:number_of_hours],\n            gci[t] - gco[t] + u_pv * pv[t] - pv_cur[t] + u_wind * wind[t] - wind_cur[t] - demand[t] + sto_to_bus[t] - sto_from_bus[t] - flow_energy2heat[t] == 0)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Heat balance","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, [t in 1:number_of_hours], -heatdemand[t] + heat_sto_to_bus[t] - heat_sto_from_bus[t] + COP*flow_energy2heat[t] - heat_losses*heat_sto_soc[t] == 0)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Naive flex potential constraint","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            if guaranteed_flex\n                @constraint(model, [t in 1:number_of_hours-1], pv_cur[t] + wind_cur[t] + sto_soc[t+1] + heat_sto_soc[t+1]/COP >= F_pos)\n                @constraint(model, [t in 1:number_of_hours-1], pv_cur[t] + wind_cur[t] - pv[t]*u_pv - wind[t]*u_wind + sto_soc[t+1] - u_storage + (heat_sto_soc[t+1] - u_heat_storage)/COP <= F_neg)\n            end","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Investment costs ... ... and background operational schedule","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @objective(model, Min, (u_pv * c_pv + u_wind * c_wind + u_storage * c_storage\n            + u_heat_storage * c_heat_storage + u_heatpump * c_heatpump) / lifetime_factor\n            + c_i * sum(gci) - c_o * sum(gco) + regularize_lossy_flows * (sum(heat_sto_from_bus) + sum(heat_sto_to_bus) + sum(sto_from_bus) + sum(sto_to_bus)))\n        end\n        @stage 2 begin\n            @parameters begin\n                recovery_time = p[:recovery_time]\n                c_i = p[:c_i]\n                c_o = p[:c_o]\n                penalty = p[:penalty]\n                heat_losses = p[:heat_losses]\n                heat_eff = p[:heat_eff]\n                sto_ef_ch = p[:sto_ef_ch]\n                sto_ef_dis = p[:sto_ef_dis]\n                COP = p[:COP]\n                max_sto_flow = p[:max_sto_flow]\n                event_per_scen = event_per_scen\n                regularize_lossy_flows = reg_lossy_flows\n                feedincap = p[:feedincap]\n            end\n            @uncertain t_xi s_xi F_xi # t_xi the time of flexibility demand, s_xi - sign (±1 or 0)\n            t_xi_final = t_xi + recovery_time","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"txi is the event time. at txi + recovery_time all variables have to match again.","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Curtailment","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @recourse(model, 0 <= pv_cur2[t in 1:1+recovery_time] <= debug_cap)\n            @recourse(model, 0 <= wind_cur2[t in 1:1+recovery_time] <= debug_cap)\n            @constraint(model, [t in 1:1+recovery_time], pv_cur2[t] <= u_pv * pv[t + t_xi - 1])\n            @constraint(model, [t in 1:1+recovery_time], wind_cur2[t] <= u_wind * wind[t + t_xi - 1])\n\n            @constraint(model, pv_cur2[1 + recovery_time] == pv_cur[t_xi + recovery_time])\n            @constraint(model, wind_cur2[1 + recovery_time] == wind_cur[t_xi + recovery_time])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Grid connection","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @recourse(model, 0 <= gci2[t in 1:1+recovery_time] <= debug_cap)\n            @recourse(model, 0 <= gco2[t in 1:1+recovery_time] <= debug_cap)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"final time equality","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, gci2[1 + recovery_time] == gci[t_xi + recovery_time])\n            @constraint(model, gco2[1 + recovery_time] == gco[t_xi + recovery_time])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Feedin cap","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, [t in 2:1+recovery_time], gco2[t] <= feedincap)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"initial time equality is more complex if we have strict flex","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            if ! regularized\n                @constraint(model, strict_flex_in, gci2[1] == gci[t_xi])\n                @constraint(model, strict_flex_out, gco2[1] == gco[t_xi])\n            end\n\n            # Utility variables to linearize min|gci[t_xi]-gci2[1]|\n            @recourse(model, 0 <= gi1 <= debug_cap)\n            @recourse(model, 0 <= gi2 <= debug_cap)\n            @constraint(model, gci2[1]-gci[t_xi] == gi1-gi2)\n            @recourse(model, 0 <= go1 <= debug_cap)\n            @recourse(model, 0 <= go2 <= debug_cap)\n            #@recourse(model, penalty_taken)\n            #@constraint(model, penalty_taken == gi1 + gi2 + go1 + go2)\n\n            @constraint(model, gco2[1]-gco[t_xi] == go1-go2)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Storage model","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @recourse(model, 0 <= sto_to_bus2[t in 1:1+recovery_time] <= debug_cap) # into the bus from storage\n            @recourse(model, 0 <= sto_from_bus2[t in 1:1+recovery_time] <= debug_cap)\n            @recourse(model, 0 <= sto_soc2[t in 1:1+recovery_time] <= debug_cap)\n            @constraint(model, [t in 1:1+recovery_time-1], sto_soc2[t+1] == sto_soc2[t] + sto_from_bus2[t] * sto_ef_ch - sto_to_bus2[t] / sto_ef_dis)\n            @constraint(model, [t in 1:1+recovery_time], sto_soc2[t] <= u_storage)\n            @constraint(model, [t in 1:1+recovery_time], sto_from_bus2[t] <= max_sto_flow * u_storage)\n            @constraint(model, [t in 1:1+recovery_time], sto_to_bus2[t] <= max_sto_flow * u_storage)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Start and end condition","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, sto_soc2[1] == sto_soc[t_xi])\n            @constraint(model, sto_soc2[1 + recovery_time] == sto_soc[t_xi + recovery_time])\n            @constraint(model,  sto_from_bus2[1 + recovery_time] == sto_from_bus[t_xi + recovery_time])\n            @constraint(model,  sto_to_bus2[1 + recovery_time] == sto_to_bus[t_xi + recovery_time])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Heat model","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @recourse(model, 0 <= heat_sto_to_bus2[t in 1:1+recovery_time] <= debug_cap) # from the heat storage\n            @recourse(model, 0 <= heat_sto_from_bus2[t in 1:1+recovery_time] <= debug_cap)\n            @recourse(model, 0 <= heat_sto_soc2[t in 1:1+recovery_time] <= debug_cap)\n            @recourse(model, 0 <= flow_energy2heat2[t in 1:1+recovery_time] <= debug_cap)\n            @constraint(model, [t in 1:1+recovery_time-1], heat_sto_soc2[t+1] == heat_sto_soc2[t] + heat_sto_from_bus2[t] * heat_eff - heat_sto_to_bus2[t] / heat_eff)\n            @constraint(model, [t in 1:1+recovery_time], heat_sto_soc2[t] <= u_heat_storage)\n            @constraint(model, [t in 1:1+recovery_time], flow_energy2heat2[t] <= (1/COP)*u_heatpump)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Start and end condition","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, heat_sto_soc2[1] == heat_sto_soc[t_xi])\n            @constraint(model, heat_sto_soc2[1 + recovery_time] == heat_sto_soc[t_xi + recovery_time])\n            @constraint(model,  heat_sto_from_bus2[1 + recovery_time] == heat_sto_from_bus[t_xi + recovery_time])\n            @constraint(model,  heat_sto_to_bus2[1 + recovery_time] == heat_sto_to_bus[t_xi + recovery_time])\n            @constraint(model,  flow_energy2heat2[1 + recovery_time] == flow_energy2heat[t_xi + recovery_time])","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Event energy balance The storage and other fast acting components use the recourse variables here. They provide the balance. Grid connection is not allowed, as we are suporting the grid here.","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, gci2[1] - gco2[1] + u_pv * pv[t_xi] - pv_cur2[1] + u_wind * wind[t_xi] - wind_cur2[1]\n             - demand[t_xi] + sto_to_bus2[1] - sto_from_bus2[1]\n             - flow_energy2heat2[1] - F_xi * s_xi == 0) # TODO CHeck that our sign convention on positive and negative flexibility agrees with literature","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Post event energy balance","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, [t in 2:1+recovery_time],\n            gci2[t] - gco2[t]\n            + u_pv * pv[t + t_xi - 1] - pv_cur2[t] + u_wind * wind[t + t_xi - 1] - wind_cur2[t]\n            - demand[t + t_xi - 1] + sto_to_bus2[t] - sto_from_bus2[t] - flow_energy2heat2[t] == 0)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"Heat balance","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @constraint(model, [t in 1:1+recovery_time], -heatdemand[t + t_xi - 1] + heat_sto_to_bus2[t] - heat_sto_from_bus2[t] + COP*flow_energy2heat2[t] - heat_losses*heat_sto_soc2[t] == 0)","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"The objective function is the difference between the adjusted schedule and the final schedule, scaled by the number of events per year. plus the penalty.","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"            @objective(model, Min, event_per_scen*(\n              c_i * (sum(gci2) - sum(gci[t_xi:t_xi_final]))\n            - c_o * (sum(gco2) - sum(gco[t_xi:t_xi_final]))\n            + penalty * (gi1 + gi2) + penalty * (go1 + go2)\n            - regularize_lossy_flows * (\n                sum(heat_sto_from_bus[t_xi:t_xi_final]) +\n                sum(heat_sto_to_bus[t_xi:t_xi_final]) +\n                sum(sto_from_bus[t_xi:t_xi_final]) +\n                sum(sto_to_bus[t_xi:t_xi_final]))\n            + regularize_lossy_flows * (\n                sum(heat_sto_from_bus2) +\n                sum(heat_sto_to_bus2) +\n                sum(sto_from_bus2) +\n                sum(sto_to_bus2))\n            ))\n        end\n    end\n    energy_system\nend\n\n\"\"\"\nGet decision variables associated with investment rather than system operation.\n\"\"\"\nget_investments(sp) = Dict((\n    :u_pv => value.(sp[1, :u_pv]),\n    :u_wind => value.(sp[1, :u_wind]),\n    :u_storage => value.(sp[1, :u_storage]),\n    :u_heatpump => value.(sp[1, :u_heatpump]),\n    :u_heat_storage => value.(sp[1, :u_heat_storage])\n))\n\n\"\"\"\nFix the investment variables.\n\"\"\"\nfunction fix_investment!(sp, investments)\n    for (var_sym, value) in zip(keys(investments), values(investments))\n        fix(decision_by_name(sp, 1, string(var_sym)), value)\n    end\nend\n\nfunction unfix_investment!(sp, investments)\n    for var_sym in keys(investments)\n        unfix(decision_by_name(sp, 1, string(var_sym)))\n    end\nend\n\n\"\"\"\nGet decision variables associated with 1st stage.\n\"\"\"\nget_operation(sp) = Dict((\n    :pv_cur => value.(sp[1,:pv_cur]),\n    :wind_cur => value.(sp[1,:wind_cur]),\n    :gci => value.(sp[1,:gci]),\n    :gco => value.(sp[1,:gco]),\n    :sto_to_bus => value.(sp[1,:sto_to_bus]),\n    :sto_from_bus => value.(sp[1,:sto_from_bus]),\n    :sto_soc => value.(sp[1,:sto_soc]),\n    :heat_sto_to_bus => value.(sp[1,:heat_sto_to_bus]),\n    :heat_sto_from_bus => value.(sp[1,:heat_sto_from_bus]),\n    :heat_sto_soc => value.(sp[1,:heat_sto_soc]),\n    :flow_energy2heat => value.(sp[1,:flow_energy2heat])\n))\n\n\"\"\"\nFix the investment variables.\n\"\"\"\nfunction fix_operation!(sp, operation, number_of_hours)\n    for (var_sym, value) in zip(keys(operation), values(operation))\n        for i in 1:number_of_hours\n            fix(decision_by_name(sp, 1, string(var_sym)*\"[$i]\"), value[i])\n        end\n    end\nend\n\nfunction unfix_operation!(sp, operation, number_of_hours)\n    for var_sym in keys(operation)\n        for i in 1:number_of_hours\n            unfix(decision_by_name(sp, 1, string(var_sym)*\"[$i]\"))\n        end\n    end\nend\n\nget_recovery(sp, n)= Dict((\n    :pv_cur2 => value.(sp[2,:pv_cur2], n),\n    :wind_cur2 => value.(sp[2,:wind_cur2], n),\n    :gci2 => value.(sp[2,:gci2], n),\n    :gco2 => value.(sp[2,:gco2], n),\n    :sto_to_bus2 => value.(sp[2,:sto_to_bus], n),\n    :sto_from_bus2 => value.(sp[2,:sto_from_bus2], n),\n    :sto_soc2 => value.(sp[2,:sto_soc2], n),\n    :heat_sto_to_bus2 => value.(sp[2,:heat_sto_to_bus2], n),\n    :heat_sto_from_bus2 => value.(sp[2,:heat_sto_from_bus2], n),\n    :heat_sto_soc2 => value.(sp[2,:heat_sto_soc2], n),\n    :flow_energy2heat2 => value.(sp[2,:flow_energy2heat2], n)\n))\n\n\"\"\"\nGet array of penalties taken in each scenario.\n0 means that scenario was satisfied without using grid connection.\n\"\"\"\nfunction get_penalty_array(sp; scens = nothing)\n    if isnothing(scens)\n        scens = scenarios(sp)\n    end\n    penalties_gci = []\n    penalties_gco = []\n    for i in eachindex(scens)\n        t_xi = scenarios(sp)[i].data.t_xi\n        push!(penalties_gci,value.(sp[2,:gci2],i)[1] - value.(sp[1,:gci])[t_xi])\n        push!(penalties_gco,value.(sp[2,:gco2],i)[1] - value.(sp[1,:gco])[t_xi])\n    end\n    return penalties_gci, penalties_gco\nend\n\nfunction get_penalized_scenarios(sp; scens = nothing)\n    if isnothing(scens)\n        scens = scenarios(sp)\n    end\n    penalized = []\n    for i in eachindex(scens)\n        t_xi = scenarios(sp)[i].data.t_xi\n        if abs(value.(sp[2,:gci2],i)[1] - value.(sp[1,:gci])[t_xi]) >= 1e-9 || abs(value.(sp[2,:gco2],i)[1] - value.(sp[1,:gco])[t_xi]) >= 1e-9\n        #if Bool(value.(sp[2,:penalty_taken],i))\n            push!(penalized,i)\n        end\n    end\n    return penalized\nend\n\nfunction get_total_investment(sp)\n    total_inv = sp.stages[1].parameters[:c_pv]*value.(sp[1, :u_pv]) +\n                sp.stages[1].parameters[:c_wind]*value.(sp[1, :u_wind]) +\n                sp.stages[1].parameters[:c_storage]*value.(sp[1, :u_storage]) +\n                sp.stages[1].parameters[:c_heat_storage]*value.(sp[1, :u_heat_storage]) +\n                sp.stages[1].parameters[:c_heatpump]*value.(sp[1, :u_heatpump])\n    return total_inv\nend\n\nfunction get_total_investment(data_dict::Dict{String, Any})\n    total_inv = sum([data_dict[\"params\"][\"c_\"*var]]*data_dict[\"inv\"][\"u_\"*var]\n        for var in [\"pv\",\"wind\",\"storage\",\"heat_storage\",\"heatpump\"])\n    return total_inv[1]\nend\n#=function first_stage_cost(sp)\n\n    return nothing\nend=#\n\nfunction get_all_data(sp)\n    inv_d = get_investments(sp)\n    op_d = get_operation(sp)\n    n = length(scenarios(sp))\n    rec_d = [get_recovery(sp, i) for i in 1:n]\n    scen_d = [Dict((:probability => s.probability, :t_xi => s.data[:t_xi],\n        :F_xi => s.data[:F_xi], :s_xi => s.data[:s_xi])) for s in scenarios(sp)]\n    params = merge(sp.stages[1].parameters, sp.stages[2].parameters)\n    return Dict((:inv => inv_d, :op => op_d, :rec => rec_d, :scen => scen_d, :params => params))\nend","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"","category":"page"},{"location":"generated/srcs/sp_model/","page":"Model","title":"Model","text":"This page was generated using Literate.jl.","category":"page"}]
